console.clear();
function chunkArrayInGroups(arr, size) {
  // Break it up.
  let res= [];
  for (let i=0;i < arr.length ; i= i+size){
    let tempArr = arr.slice(i,i+size);
    res.push(tempArr);
    // console.log(res,arr);
  }
  return res;
}

console.log(chunkArrayInGroups(["a", "b", "c", "d"], 2));
