// the global Array
var s = [23, 65, 98, 5];

Array.prototype.myFilter = function(callback){
  var newArray = [...this];
  let res=[];
  // Add your code below this line
  newArray.forEach(item => {
     if(callback(item)== true){
       res.push(item);
     } ;
  });
  // Add your code above this line
  return res;

};

var new_s = s.myFilter(function(item){
  return item % 2 === 1;
});
