console.clear();

function addTogether() {
    // Solution1 
    let args = [...arguments];
    if (args.every(x => typeof x == "number") == false) {
        return undefined;
    } else {
        if (args.length > 1) {
            return args.reduce((a, c) => a + c);
        } else {
            return function () {
                let arg = args.concat([...arguments]);
                if (arg.every(x => typeof x == "number") == false) {
                    return undefined;
                } else {
                    return arg.reduce((b, d) => b + d)
                }
            }
        }
    }
    // return args.every(x => typeof x !== "number");

}

