console.clear();
function dropElements(arr, func) {
  // Drop them elements.
  let proArr = [...arr];
  for(let i = 0 ; i < proArr.length ;i++){
    if(func(proArr[i]) === true){
      return proArr.slice(i,)
    }
  }
  return [];
}

console.log(dropElements([1, 2, 3, 4], function(n) {return n > 5;}));
