    newNames.unshift(name);
    person = newNames.join(" ")
  }
  this.setLastName = function(name){
    let newNames = firstAndLast.split(" ")
    newNames.pop();
    newNames.push(name);
    person = newNames.join(" ")
  }
  this.setFullName = function(name){
    person = name;
  }

