console.clear();
function smallestCommons(arr) {
  let nums = [];
  let order = arr.sort((a,b) => a-b); //sort input numbers
  // create an array of all numbers needed to calculate the LCM for
  for( let i = order[0] ; i<=order[1] ; i++){
    nums.push(i)
  }
  let max = nums.reduce((a,b) => a*b); // create max value to prevent for loop working for ever and crack
  // check if the biggest number multiplication is divisable for all other nums array elements 
  // create a for loop with a step equals the highest number of the nums array, then check it fo the other elements 
  for (let j = 0 ; j < max ; j = j+order[1]){
    if ( nums.every(x => j % x === 0) == true && j !== 0) {
    return j ; //returns the smallest multib=ble number of the highest number of the nums array that are divisable on all nums array elements
    }
  }
}


console.log(smallestCommons([2,10]));
