console.clear();
function rot13(str) { // LBH QVQ VG!
  let splStr = str.split(" ");
  let newStr = [];
  splStr.map(word => {
      let splWord = word.split("");
      let newWord = [];
      splWord.map(l => {
          let code = l.charCodeAt(0);
          if (code > 64 && code < 78){
              code += 13
          } else if (code >= 78 && code < 93){
              code -= 13
          }
          newWord.push(String.fromCharCode(code));
      })
        newStr.push(newWord);
  });
//   return String.fromCharCode(65);
// return "N".charCodeAt(0)
return newStr.map(arr => arr.join("")).join(" ")
}

