console.clear();

function checkCashRegister(price, cash, cid) {
    let output;
    let change = cash - price;
    let newCid = [];
    const check = ((unit, str, cid) => {
        let taken = 0;
        let div, unitCash;
        unitCash = cid.filter(arr => arr[0] == str)[0][1];
        div = Math.floor(change / unit)
        taken = div * unit;
        let diff = unitCash - taken;
        if (diff > 0) {
            cid.map(arr => {
                if (arr[0] === str) {
                    arr[1] = diff
                }
            });
            newCid.push([str, taken]);
            change -= taken;
        } else {
            cid.map(arr => {
                if (arr[0] === str) {
                    arr[1] = 0
                }
            });
            newCid.push([str, unitCash]);
            change -= unitCash;
        }
    });
    const checkStatus = ((change, cid, newCid) => {
        console.log(change, cid, newCid);
        if (change > 0) {
            output = {
                status: "INSUFFICIENT_FUNDS",
                change: []
            }
        } else {
            let cidCheck = cid.every(arr => arr[1] === 0);
            if (cidCheck === true) {
                let newCidUnits = newCid.map(arr => arr[0]);
                cid = cid.filter(arr => newCidUnits.includes(arr[0]) == false);
                output = {
                    status: "CLOSED",
                    change: newCid.reverse().concat(cid)
                }
            } else {
                output = {
                    status: "OPEN",
                    change: newCid
                }
            }
        }
    });
    if (change >= 100) {
        check(100, "ONE HUNDRED", cid);
    }
    if (change >= 20) {
        check(20, "TWENTY", cid);
    }
    if (change >= 10) {
        check(10, "TEN", cid);
    }
    if (change >= 5) {
        check(5, "FIVE", cid);
    }
    if (change >= 1) {
        check(1, "ONE", cid);
    }
    if (change >= 0.25) {
        check(0.25, "QUARTER", cid);
    }
    if (change >= 0.1) {
        check(0.1, "DIME", cid);
    }
    if (change >= 0.05) {
        check(0.05, "NICKEL", cid);
    }
    if (change >= 0.01) {
        //https://stackoverflow.com/questions/11695618/dealing-with-float-precision-in-javascript
        //https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/Number/toFixed 
        change = change.toFixed(2);
        check(0.01, "PENNY", cid);
    }
    checkStatus(change, cid, newCid);
    console.log(output);
    return output
}


