console.clear();

function convertToRoman(num) {
    let strNumArr = num.toString().split("").reverse();

    let roman = {
        1: ["I", "X", "C", "M"],
        4: ["IV", "XL", "CD"],
        5: ["V", "L", "D"],
        9: ["IX", "XC", "CM"]
    }
    // ["6", "30", "500", "4000"]

    let numArr = [];
    strNumArr.map((num, i) => {
        if(num <= 3){
            numArr.push(roman[1][i].repeat(num))
        } else if(5 < num && num <= 8){
            numArr.push(roman[5][i] + roman[1][i].repeat(num-5))
        } else if (num == 4 || num == 5 || num == 9){
            numArr.push(roman[num][i])
        }
    });

    return numArr.reverse().join("");
}

console.log(convertToRoman(3999));
